    const fetchAddressDetailsFromPinCode = async (pincode) => {
        axios.get('https://api.postalpincode.in/pincode/' + pincode)
        .then(function (response) {
          if (response && response.data.length>0) {
            setUserReg({ 
              ...userReg,
              pincode:pincode,
              village: response.data[0].PostOffice[0].Block, 
              district: response.data[0].PostOffice[0].District, 
              state: response.data[0].PostOffice[0].State, 
              loadOpen: false 
            })
          }
          else {
            console.log('no data found');
          }
        })
        .catch((error) => {
          console.log(error);
        });
      }
  
      const filterPincode = (x) => {
        const pattern = /^[0-9]{6}$/;
        
          if(x.length === 6 && pattern.test(x) ){
            fetchAddressDetailsFromPinCode(x);
          }else{
            let apiUrl =
            "https://parseapi.back4app.com/classes/Dataset_India_Pin_Code?limit=5&where=" +
            encodeURIComponent(
              JSON.stringify({
                postalCode: {
                  "$regex": x
                },
              })
            );
            fetchPincodeSuggestions(apiUrl);
          }
          
      }


