import React, { useEffect, useState } from 'react'

import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select, { SelectChangeEvent } from '@mui/material/Select';
import { AdapterMoment } from '@mui/x-date-pickers/AdapterMoment';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import Backdrop from '@mui/material/Backdrop';
import CircularProgress from '@mui/material/CircularProgress';
import { DesktopDatePicker } from '@mui/x-date-pickers/DesktopDatePicker';
import uniq from "lodash/uniq";
import _ from 'lodash';
import axios from 'axios';
//const axios = require('axios');


const Form = () => {

    const[userReg, setUserReg] = useState({
        firstname:'',
        lastname: '',
        email: '',
        phonenumber: '',
        gender: '',
        dob: '',
        address: '',
        address2: '',
        pincode: '',
        village: '',
        district: '',
        state: '',
        loadOpen: false,
        pincodesArray: []
    }
    );

    const [formErrors, setFormErrors] = useState({});
    const [isSubmit, setIsSubmit] = useState(false);
    const[records, setRecords] = useState([]);

    const inputHandler = (e) => {
        const name = e.target.name;
        const value = e.target.value;

        setUserReg({...userReg, [name] : value});
    }


    useEffect(() => {
      const fetchData = async () => {
        var root =
          "https://parseapi.back4app.com/classes/Dataset_India_Pin_Code?limit=20&where=" +
          encodeURIComponent(
            JSON.stringify({
              countryCode: "IN",
            })
          );

        console.log("test", root);

        // Make a GET request
        var headers = {
          headers: {
            "X-Parse-Application-Id":
              "G7Z8d2KcYmU0WsiPgFIZS43FUBLvarKdx5MtgyLs", // This is the fake app's application id
            "X-Parse-Master-Key": "TRxCvj4TyIJFd2NBs5XyXeQxAqUpm7SageFJ2sUZ", // This is the fake app's readonly master key
          },
        };
        axios
          .get(root, headers)
          .then((response) => {
            console.log("data", response.data);
            const distintValues = _.uniqBy(response.data.results, "postalCode");
            console.log(distintValues);
            setUserReg({ ...userReg,pincodesArray: distintValues });
            setTimeout(() => {}, 3000);

            console.log(userReg);
          })
          .catch((e) => {
            console.log("error", e);
          });
      };
      fetchData();
    }, []);

  const handleChange = (event) => {

    setUserReg({ ...userReg,gender: event.target.value })
    console.log(event.target.value)

  }

  const handleChangePincode = (event) => {
    const pincode = event.target.value;
    setUserReg({ ...userReg,pincode: pincode, loadOpen: true })

    console.log(event.target.value)


    axios.get('https://api.postalpincode.in/pincode/' + event.target.value,)
      .then(function (response) {
        if (response) {
          console.log(response.data[0]);
          console.log(response.data[0].PostOffice[1]);
          setUserReg({ 
            ...userReg,
            pincode:pincode,
            village: response.data[0].PostOffice[0].Block, 
            district: response.data[0].PostOffice[0].District, 
            state: response.data[0].PostOffice[0].State, 
            loadOpen: false 
          })
        }
        else {
          console.log('error');
        }
      })
      .catch((error) => {
        console.log(error);
      });

  }

  const handleSubmit = (e) => {
    e.preventDefault();

    const newRecord = {...userReg, id : Math.random.toString()}
    console.log(records);
    setRecords([...records, newRecord]);

    setUserReg({
        firstname:'',
    lastname: '',
    email: '',
    phonenumber: '',
    gender: '',
    dob: '',
    address: '',
    address2: '',
    pincode: '',
    city: '',
    district: '',
    state: ''
    
    });

    //setFormErrors(validate(userReg));
    setIsSubmit(true);
}


    return (
      <div className="row m-4">
        <div className="col-md-4 mx-auto">
          <Card variant="outlined">
            <CardContent>
              <h2 className='text-center'>Sign Up Form</h2>

              <div className='row'>
                <div className='col-md-6 mt-3'>
                  <TextField onChange={inputHandler} name="firstname" value={userReg.firstname} id="outlined-basic" fullWidth label="First Name" required variant="outlined" />
                </div>

                <div className='col-md-6 mt-3'>
                  <TextField onChange={inputHandler} name="lastname" value={userReg.lastname} id="outlined-basic" fullWidth label="Last Name" required variant="outlined" />
                </div>

                <div className='col-md-6 mt-3'>
                  <TextField onChange={inputHandler} value={userReg.email} name="email" id="outlined-basic" fullWidth label="Email" required variant="outlined" />
                </div>

                <div className='col-md-6 mt-3'>
                  <TextField onChange={inputHandler} value={userReg.phonenumber} name="phonenumber" id="outlined-basic" inputProps={{ maxLength: 10 }} fullWidth label="Phone" required variant="outlined" error={false} helperText="" />
                </div>

                <div className='col-md-6 mt-3'>
                  <FormControl fullWidth>
                    <InputLabel id="demo-simple-select-label">Gender</InputLabel>
                    <Select
                      labelId="demo-simple-select-label"
                      id="demo-simple-select"
                      name="gender"
                      value={userReg.gender}
                      label="Gender"
                      required
                      onChange={handleChange}
                    >
                      <MenuItem value='male'>Male</MenuItem>
                      <MenuItem value='female'>Female</MenuItem>
                    </Select>
                  </FormControl>
                </div>

                <div className='col-md-6 mt-3'>
                  <LocalizationProvider dateAdapter={AdapterMoment}>
                    <DesktopDatePicker
                      label="Date desktop"
                      inputFormat="DD/MM/YYYY"
                      name="dob"
                      value={userReg.dob}
                      error={false}
                      onChange={(newValue) => {
                        setUserReg({...userReg, dob: newValue }),{inputHandler}
                      }}
                      renderInput={(params) => <TextField fullWidth {...params} />}
                    />
                  </LocalizationProvider>
                </div>

                <div className='col-md-12 mt-3'>
                  <TextField onChange={inputHandler} name="address" value={userReg.address} id="outlined-basic" multiline maxRows={4} fullWidth label="Address 1" required variant="outlined" />
                </div>

                <div className='col-md-12 mt-3'>
                  <TextField onChange={inputHandler} value={userReg.address2} name="address2" id="outlined-basic" multiline maxRows={4} fullWidth label="Address 2" required variant="outlined" />
                </div>

                <div className='col-md-6 mt-3'>
                  <FormControl fullWidth>
                    <InputLabel id="demo-simple-select-label">Pincode</InputLabel>
                    <Select
                      labelId="demo-simple-select-label"
                      id="demo-simple-select"
                      name="pincode"
                      value={userReg.pincode}
                      label="Pincode"
                      required
                      onChange={handleChangePincode}
                    >

                      {uniq(userReg.pincodesArray).map((pincode) => (
                        <MenuItem value={pincode.postalCode}>{pincode.postalCode}</MenuItem>
                      ))}
                    </Select>
                  </FormControl>
                </div>

                <div className='col-md-6 mt-3'>
                  <TextField  disabled id="outlined-basic" name="village" value={userReg.village} fullWidth label="City/Village" required variant="outlined" />
                </div>

                <div className='col-md-6 mt-3'>
                  <TextField disabled id="outlined-basic" name="district" value={userReg.district} fullWidth label="District" required variant="outlined" />
                </div>

                <div className='col-md-6 mt-3'>
                  <TextField disabled id="outlined-basic" name="state" value={userReg.state} fullWidth label="State" required variant="outlined" />
                </div>

              </div>
            </CardContent>
            <CardActions>
              <Button onClick={handleSubmit}  fullWidth variant='contained' style={{ paddingTop: 8, paddingBottom: 8 }}  disableElevation>Submit</Button>
            </CardActions>
          </Card>

          {/* Loading Backdrop Start */}
          <Backdrop
            open={userReg.loadOpen}
          >
            <CircularProgress color="inherit" />
          </Backdrop>
          {/* Loading Backdrop End */}
        </div>
      </div>
    );
  }

  export default Form;
